import XCTest

@testable import EvervaultCore

class asn1Test: XCTestCase {

    let curveValues = curveConstants

    func testAsn1() {

        XCTAssertEqual(ASN1.encode("04", curveValues.a), "0420ffffffff00000001000000000000000000000000fffffffffffffffffffffffc")
        XCTAssertEqual(ASN1.encode("04", curveValues.b), "04205ac635d8aa3a93e7b3ebbd55769886bc651d06b0cc53b0f63bce3c3e27d2604b")
        XCTAssertEqual(ASN1.encode("06", "2A 86 48 CE 3D 02 01"), "06072a8648ce3d0201")
        XCTAssertEqual(ASN1.encode(
            "30",
            ASN1.encode("04", curveValues.a), // curve a value
            ASN1.encode("04", curveValues.b), // curve b value
            ASN1.BITSTR(curveValues.seed) // curve seed value
        ), "305b0420ffffffff00000001000000000000000000000000fffffffffffffffffffffffc04205ac635d8aa3a93e7b3ebbd55769886bc651d06b0cc53b0f63bce3c3e27d2604b031500c49d360886e704936a6678e1139d26b7819f7e90")

        var encoded = ASN1.encode(
            "30",
            ASN1.encode(
                "30",
                ASN1.encode("06", "2A 86 48 CE 3D 02 01"), // 1.2.840.10045.2.1 ecPublicKey
                ASN1.encode(
                    "30",
                    ASN1.UINT("01"), // ECParameters Version
                    ASN1.encode(
                        "30",
                        ASN1.encode("06", "2A 86 48 CE 3D 01 01"), // X9.62 Prime Field
                        ASN1.UINT(curveValues.p) // curve p value
                    ),
                    ASN1.encode(
                        "30",
                        ASN1.encode("04", curveValues.a), // curve a value
                        ASN1.encode("04", curveValues.b), // curve b value
                        ASN1.BITSTR(curveValues.seed) // curve seed value
                    ),
                    ASN1.encode("04", curveValues.generator), // curve generate point in decompressed form
                    ASN1.UINT(curveValues.n), // curve n value
                    ASN1.UINT(curveValues.h) // curve h value
                )
            )
        )
        XCTAssertEqual(encoded, "")

        let hexKey = "04083b583132ee557bbcc8272e943183737be0ea5f4689e167e76fd5dd19f8670ee82fd70f39501e2b972e5773cb6aaa0eb3d4efdf578c1dd96a945fbd19432c15"
        encoded = ASN1.encode(
            "30",
            ASN1.encode(
                "30",
                ASN1.encode("06", "2A 86 48 CE 3D 02 01"), // 1.2.840.10045.2.1 ecPublicKey
                ASN1.encode(
                    "30",
                    ASN1.UINT("01"), // ECParameters Version
                    ASN1.encode(
                        "30",
                        ASN1.encode("06", "2A 86 48 CE 3D 01 01"), // X9.62 Prime Field
                        ASN1.UINT(curveValues.p) // curve p value
                    ),
                    ASN1.encode(
                        "30",
                        ASN1.encode("04", curveValues.a), // curve a value
                        ASN1.encode("04", curveValues.b), // curve b value
                        ASN1.BITSTR(curveValues.seed) // curve seed value
                    ),
                    ASN1.encode("04", curveValues.generator), // curve generate point in decompressed form
                    ASN1.UINT(curveValues.n), // curve n value
                    ASN1.UINT(curveValues.h) // curve h value
                )
            ),
            ASN1.BITSTR(hexKey)
        )
        XCTAssertEqual(encoded, "")
    }
}
